(function(){'use strict';var EsGoogleMap=function(selector,lon,lat,zoom){this.selector=selector;this.zoom=zoom||16;this.lon=parseFloat(lon);this.lat=parseFloat(lat);this.markers=[];this.init=function(){this.instance=new google.maps.Map(this.selector,{zoom:this.zoom,center:{lat:this.lat,lng:this.lon}});google.maps.event.trigger(this.selector,'resize');return this;};this.setMarker=function(position){position={lat:this.lat,lng:this.lon}||position;this.markers.push(new google.maps.Marker({position:position,map:this.instance}));return this;};this.getGeocoderInfo=function(lat,lon,callback){var geocoder=new google.maps.Geocoder();var latLon=new google.maps.LatLng(lat,lon);geocoder.geocode({latLng:latLon},callback);};return this;};window.EsGoogleMap=EsGoogleMap;})();